import React, { useEffect,useState} from "react";

export default  ItemDetail = (props) => {

    const [state, setState] = useState(false);
    const [newData, setNewData] = useState(props.data);

    if(!state){
        return(
            <>
                <h5>{props.detail}</h5>
                <p>{props.data}</p>
                <button onClick={() => setState(true)}>Edit</button>
            </>
        )
    }
    else{
        return(
            <>
                <p>{props.data}</p>
                <input type="text" onChange={(e) => setNewData(e.target.value)}></input>
                <button onClick={()=> {
                    props.editItem(props.id, newData, props.detail)
                    setState(false);
                }}></button>
            </>
        )
    }
}
