import React, { useEffect,useState} from "react";

export default function ItemDetail(props){

    const [state, setState] = useState(false);
    const [newData, setNewData] = useState(props.data);

    useEffect(() => {
        if(newData === 0){
            setNewData(props.data);
        }
    },[newData]);

    if(!state){
        return(
            <>
            <div className="d-flex flex-row bd-highlight mb-3">
                <h5>{props.detail}</h5>
                <p>{props.data}</p>
                <button onClick={() => setState(true)}>Edit</button>
            </div>
            </>
        )
    }
    else{
        return(
            <>
                <p>{props.data}</p>
                <input type="text" onChange={(e) => setNewData(e.target.value)}></input>
                <button onClick={()=> {
                    setNewData(props.editItem(props.id, newData, props.detail))
                    setState(false);
                }}>Validate</button>
            </>
        )
    }
}
