const Vis = () => {
    const { useRef, useEffect, useState } = React
    const mount = useRef(null)
    const [isAnimating, setAnimating] = useState(true)
    const controls = useRef(null)

    useEffect(() => {
      let width = mount.current.clientWidth
      let height = mount.current.clientHeight
      let frameId

      const scene = new THREE.Scene()
      const camera = new THREE.PerspectiveCamera(75, width / height, 0.1, 1000)
      const renderer = new THREE.WebGLRenderer({ antialias: true })
      const geometry = new THREE.BoxGeometry(1, 1, 1)
      const material = new THREE.MeshBasicMaterial({ color: 0xff00ff })
      const cube = new THREE.Mesh(geometry, material)

      camera.position.z = 4
      scene.add(cube)
      renderer.setClearColor('#000000')
      renderer.setSize(width, height)

      const renderScene = () => {
        renderer.render(scene, camera)
      }

      const handleResize = () => {
        width = mount.current.clientWidth
        height = mount.current.clientHeight
        renderer.setSize(width, height)
        camera.aspect = width / height
        camera.updateProjectionMatrix()
        renderScene()
      }

      const animate = () => {
        cube.rotation.x += 0.01
        cube.rotation.y += 0.01

        renderScene()
        frameId = window.requestAnimationFrame(animate)
      }

      const start = () => {
        if (!frameId) {
          frameId = requestAnimationFrame(animate)
        }
      }

      const stop = () => {
        cancelAnimationFrame(frameId)
        frameId = null
      }

      mount.current.apendChild(renderer.domElement)
      window.addEventListener('resize', handleResize)
      start()

      controls.current = { start, stop }
      renderer.setSize(window.innerWidth, window.innerHeight);
      mount.current.appendChild(renderer.domElement);

      const loader = new GLTFLoader();
      loader.load('https://lauriaristorage.blob.core.windows.net/objects/aa78b917-914f-4fda-bd1b-4e9ff8972e77/corridorFloor.gltf', function(gltf){
          scene.add(gltf.scene);
      });
      const light = new THREE.PointLight(0xffffff, 2, 200);
      light.position.set(4.5,10,4.5);
      scene.add(light);

      return () => {
        stop()
        window.removeEventListener('resize', handleResize)
        mount.current.removeChild(renderer.domElement)

        scene.remove(cube)
        geometry.dispose()
        material.dispose()
      }
    }, [])





    useEffect(() => {
      if (isAnimating) {
        controls.current.start()
      } else {
        controls.current.stop()
      }
    }, [isAnimating])

    return <div className="vis" ref={mount} onClick={() => setAnimating(!isAnimating)} />
  }

  export default Vis;
