import * as THREE from "../node_modules/three/build/three.module.js"
import {OrbitControls} from "../node_modules/three/examples/jsm/controls/OrbitControls.js"
import {GLTF, GLTFLoader} from "../node_modules/three/examples/jsm/loaders/GLTFLoader.js"
import React, { useEffect,useRef} from "react";

export default function GLTFView(props){
    const ref = useRef();
    const scene = new THREE.Scene();
    const camera = new THREE.PerspectiveCamera(75, window.innnerWidth / window.innerHeight, 0.1, 1000);

    useEffect(() => {
        scene = new THREE.Scene();
        camera = new THREE.PerspectiveCamera(75, window.innnerWidth / window.innerHeight, 0.1, 1000);
    })


        const renderer = new THREE.WebGLRenderer();
        renderer.setSize(window.innerWidth, window.innerHeight);
        ref.current.appendChild(renderer.domElement);

        const loader = new GLTFLoader();
        loader.load('https://lauriaristorage.blob.core.windows.net/objects/aa78b917-914f-4fda-bd1b-4e9ff8972e77/corridorFloor.gltf', function(gltf){
            scene.add(gltf.scene);
        });
        const light = new THREE.PointLight(0xffffff, 2, 200);
        light.position.set(4.5,10,4.5);
        scene.add(light);




    return(
        <>
            <div ref={ref}></div>
        </>
    )
}
